import './App.css';
import { useState } from 'react';
// import Header from './Header';
// import Nav from './Nav';
// import Content from './Content'
// import RightMenu from './RightMenu';
// import Footer from './Footer';

function App() {
  const [degreeF, setDegreeF] = useState(0)
  const [degreeC, setDegreeC] = useState(0)
  function handleChange1(event){
    setDegreeF(+event.target.value);
  }
  function handleChange2(event){
    setDegreeC(+event.target.value);
  }
  return (

  <div>
    <span>F° = </span>
    <input value={degreeC * 33.8} onChange={handleChange1}/><br/>
    <span>C° = </span>
    <input value={degreeF / 33.8} onChange={handleChange2}/><br/>

  </div>
  );
}

